--CREATE SEQUENCE vehicle_seq START WITH 1;
--drop cascade table if exists "assets_schema"."vehicle";
create table if not exists  "assets_schema"."vehicle"("id" UUID not null,
           "name" Character varying(30) not null, "model" Character varying(50) not null,
           "type" Character varying(30) not null, "reg_no" Character varying(50) not null,
            "last_service" date, "next_service" date not null, "user_id" uuid default null,
            primary key("id"), foreign key("user_id") references
            "assets_schema"."user"("id")) ;

create index if not exists  reg_no_uk on "assets_schema"."vehicle" using btree("reg_no");
